mapper.select:
SELECT DISTINCT kb.id
FROM knowledge_bases kb
LEFT JOIN user_knowledge_base_access acc
    ON acc.kb_id = kb.id AND acc.user_id = #{userId}
WHERE kb.is_active = 1
  AND (
    kb.type = 'SHARED'
    OR kb.owner_id = #{userId}
    OR acc.role IS NOT NULL
  )

mapper.insert:
INSERT INTO knowledge_bases (
    id, name, description, type, owner_id, is_active, created_at, updated_at
) VALUES (
    #{id}, #{name}, #{description}, #{type}, #{ownerId}, #{isActive},
    NOW(), NOW()
)
